prompt --application/shared_components/security/authorizationsbeginwwv_flow_api.create_security_scheme( p_id=>wwv_flow_api.id(328224405980893970),p_name=>'Is_admin',p_scheme_type=>'NATIVE_FUNCTION_BODY',p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2('DECLARE','  loc_count_admin INTEGER := 0;','BEGIN','  select count(role_code)','  into loc_count_admin ','  from SOK_USER_ROLE_VW','  where user_email = APEX_APPLICATION.g_user AND role_code = ''ADMIN'';','  ','  if loc_count_admin > 0 THEN','    return true;','  else','    return false;','  end if;','EXCEPTION','WHEN OTHERS THEN','   return false;','END; ')),p_error_message=>'Restricted access: Administrators Only',p_caching=>'BY_USER_BY_SESSION');wwv_flow_api.create_security_scheme( p_id=>wwv_flow_api.id(328321042163774142),p_name=>'Is_user',p_scheme_type=>'NATIVE_FUNCTION_BODY',p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2('DECLARE','  loc_count_admin INTEGER := 0;','BEGIN','  select count(role_code)','  into loc_count_admin ','  from SOK_USER_ROLE_VW','  where user_email = APEX_APPLICATION.g_user AND role_code = ''USER'';','  ','  if loc_count_admin > 0 THEN','    return true;','  else','    return false;','  end if;','EXCEPTION','WHEN OTHERS THEN','   return false;','END; ')),p_error_message=>'Restricted access : Users Only',p_caching=>'BY_USER_BY_SESSION');end;/